var util = require( "../src/js/util.js" );

// 得到一个集合中所有未解决的err
var findAllUnsolvedErr = function ( db, collectionName, callback ) {
    db.collection( collectionName, {strict : true}, function ( err, collection ) {
        if ( err ) {
            callback( "不存在" + collectionName );
        }
        else {
            collection.aggregate(
                [
                    {
                        $match : {
                            solve : false
                        }
                    },
                    {
                        $group : {
                            _id : "$err",
                            count : {
                                $sum : 1
                            }
                        }
                    },
                    {
                        $sort : {
                            count : -1
                        }
                    }
                ],
                function ( err, result ) {
                    callback( err ? err.toString() : null, result );
                }
            );
        }
    } );
};

// 得到集合列表
var getCollectionList = function ( db, callback ) {
    db.collections( function ( err, collections ) {
        if ( err ) {
            callback( err.toString() );
        }
        else {
            var result = [];
            util.forEach( collections, function ( item ) {
                item.s.name != "system.indexes" && result.push( {
                    name : item.s.name
                } )
            } );
            callback( null, result );
        }
    } );
};

// 得到一个项目在今天每小时出现的次数
var getUnsolvedErrCountInTodayByHours = function ( db, collectionName, callback ) {
    db.collection( collectionName, {strict : true}, function ( err, collection ) {
            if ( err ) {
                callback( "不存在" + collectionName );
            }
            else {
                var d = new Date();
                collection.aggregate(
                    [
                        {
                            $match : {
                                solve : false
                            }
                        },
                        {
                            $project : {
                                year : {$year : "$date"},
                                month : {$month : "$date"},
                                day : {$dayOfMonth : "$date"},
                                err : 1
                            }
                        },
                        {
                            $match : {
                                year : d.getYear(),
                                month : d.getMonth(),
                                day : d.getDate()
                            }
                        }
                    ],
                    function ( err, result ) {

                    }
                )
            }
        }
    );
};


exports.findAllUnsolvedErr = findAllUnsolvedErr;
exports.getCollectionList = getCollectionList;
exports.getUnsolvedErrCountInTodayByHours = getUnsolvedErrCountInTodayByHours;